public static IHostBuilder CreateHostBuilder(string[] args) =>
          Host.CreateDefaultBuilder(args)
              .ConfigureAppConfiguration((hostingContext, config) =>
              {
                  config.AddJsonFile("appsettings.json", optional: false, reloadOnChange: true);
              }

OnConfiguring.raw:
protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)
        {
            optionsBuilder
                .UseLoggerFactory(MyLoggerFactory)  // LoggerFactory should be defined
                .EnableSensitiveDataLogging()
                .UseSqlServer(
                    optionsBuilder.Options.FindExtension<Microsoft.EntityFrameworkCore.SqlServer.Infrastructure.Internal.SqlServerOptionsExtension>()?.ConnectionString,
                    sqlServerOptionsAction: sqlOptions =>
                    {
                        sqlOptions.CommandTimeout(300);
                        sqlOptions.EnableRetryOnFailure(
                            maxRetryCount: 5, // Número máximo de tentativas
                            maxRetryDelay: TimeSpan.FromSeconds(10), // Tempo máximo de espera entre tentativas
                            errorNumbersToAdd: null); // Deixe nulo para usar os padrões
                    });
        }

