ConfigureDistAssociacao.raw:
private void ConfigureDistAssociacao(EntityTypeBuilder<DistAssociacao> builder)
        {
            builder.ToTable("DIST_ASSOCIACAO");
            builder.HasKey(e => e.CodDistAssociacao);
            builder.Property(e => e.CodDistAssociacao).HasColumnName("cod_distassociacao");

            builder.Property(e => e.VlrAssociacao)
                   .HasPrecision(18, 9)
                   .HasColumnName("vlr_associacao");

            builder.Property(e => e.VlrAssocConvPctVerba)
                   .HasPrecision(18, 9)
                   .HasColumnName("vlr_assocconvpctverba");

            builder.Property(e => e.VlrAssocCorrecao)
                   .HasPrecision(18, 9)
                   .HasColumnName("vlr_assoccorrecao");

            builder.HasOne<DistTitular>() 
                   .WithMany()
                   .HasForeignKey(e => e.CodDistTitular);
        }

