GerarConsolidacaoConexa.raw:
public IEnumerable<ConsolidacaoCC> GerarConsolidacaoConexa()
        {
            if (this.Fonogramas.Count() > 0)
            {
                var verbaConexa = (Competencia.VlrVerbaConexa * VlrFatorDistribuicao) / Competencia.VerbaTotal;

                var consolidacoesCC = Fonogramas.Select(x => new ConsolidacaoCC()
                {
                    CodAudioVisual = this.CodObraAudioEcad,
                    CodFonograma = x.Fonograma,
                    Categoria = x.CaracteristicaDistribuicao,
                    OcorrenciasAudiovisual = this.NroFatorDistribuicao,
                    ExecucoesMusica = x.QtdDuracaoMusica,
                    PesoDaCategoria = EnumHelper.GetEnumValue(x.CaracteristicaDistribuicao)
                }).ToList();

                var group = consolidacoesCC.GroupBy(item => item.Categoria)
                            .Select(grupo => new
                            {
                                Categoria = grupo.Key,
                                Soma = grupo.Sum(item => item.ExecucoesMusica)
                            });

                foreach (var cc in consolidacoesCC)
                {
                    cc.SomaExecucaoDasCaracteristica = group.Where(group => group.Categoria == cc.Categoria).Single().Soma;
                    cc.TotalExecucoes = cc.SomaExecucaoDasCaracteristica * cc.OcorrenciasAudiovisual;
                    cc.TotalExecucoesXPesoCategoria = cc.TotalExecucoes * cc.PesoDaCategoria.Value;

                }

                var distinct = consolidacoesCC.DistinctBy(p => p.Categoria);

                var group2 = distinct.GroupBy(item => item.Categoria)
                        .Select(grupo => new
                        {
                            Categoria = grupo.Key,
                            Soma = grupo.Sum(item => item.TotalExecucoesXPesoCategoria)
                        });


                foreach (var aa in consolidacoesCC)
                {
                    var soma = group2.Sum(g => g.Soma);
                    aa.Ponto = (verbaConexa / soma) * (aa.TotalExecucoesXPesoCategoria / aa.TotalExecucoes);


                };


                Console.WriteLine("Consolidacao CC");
                Console.WriteLine(JsonSerializer.Serialize(consolidacoesCC));

                return consolidacoesCC;
            }
            else
                return null;
        }

GerarConsolidacaoAutoral.raw:
public IEnumerable<ConsolidacaoAA> GerarConsolidacaoAutoral()
        {
            try
            {
                if (this.ObrasMusicais.Count() > 0)
                {
                    var verbaAutoral = (Competencia.VlrVerbaAutoral * VlrFatorDistribuicao) / Competencia.VerbaTotal;

                    var consolidacoesAA = ObrasMusicais.Select(x => new ConsolidacaoAA()
                    {
                        CodAudioVisual = this.CodObraAudioEcad,
                        CodObra = x.CodObraMusEcad,
                        Categoria = x.CaracteristicaDistribuicao,
                        OcorrenciasAudiovisual = this.NroFatorDistribuicao,
                        ExecucoesMusica = x.QtdDuracaoMusica,
                        PesoDaCategoria = EnumHelper.GetEnumValue(x.CaracteristicaDistribuicao)
                    }).Where(x => x.TipParteRepresentada == "AA").ToList();

                    var group = consolidacoesAA.GroupBy(item => item.Categoria)
                                .Select(grupo => new
                                {
                                    Categoria = grupo.Key,
                                    Soma = grupo.Sum(item => item.ExecucoesMusica)
                                });

                    foreach (var cc in consolidacoesAA)
                    {
                        cc.SomaExecucaoDasCaracteristica = group.Where(group => group.Categoria == cc.Categoria).Single().Soma;
                        cc.TotalExecucoes = cc.SomaExecucaoDasCaracteristica * cc.OcorrenciasAudiovisual;
                        cc.TotalExecucoesXPesoCategoria = cc.TotalExecucoes * cc.PesoDaCategoria.Value;
                    }

                    var distinct = consolidacoesAA.DistinctBy(p => p.Categoria);

                    var group2 = distinct.GroupBy(item => item.Categoria)
                            .Select(grupo => new
                            {
                                Categoria = grupo.Key,
                                Soma = grupo.Sum(item => item.TotalExecucoesXPesoCategoria)
                            });


                    foreach (var aa in consolidacoesAA)
                    {
                        var soma = group2.Sum(g => g.Soma);
                        aa.Ponto = (verbaAutoral / soma) * (aa.TotalExecucoesXPesoCategoria / aa.TotalExecucoes);
                    }

                    Console.WriteLine("Consolidacao AA");
                    Console.WriteLine(JsonSerializer.Serialize(consolidacoesAA));

                    return consolidacoesAA;
                }
                else
                    return null;
            }
            catch (Exception ex) { throw ex; }
        }

