GetInterpretes.raw:
public string GetInterpretes(Int64 CodFonograma)
        {
            if (TitularesFonograma != null && TitularesFonograma.Count() > 0)
            {
                string interpretes = string.Empty;

                foreach (var titFono in TitularesFonograma.Where(x => x.CodCategoria.Trim() == "I" && x.CodFonograma == CodFonograma))
                {
                    interpretes += titFono.NomTitular + " / ";
                }

                interpretes = interpretes.Length > 3 ? interpretes.Substring(0, interpretes.Length - 3) : interpretes;

                return interpretes;
            }
            else
                return "";         
        }

GerarConsolidacao.raw:
public static ConsolidacaoDTO GerarConsolidacao(AudioVisualGloboplay audioVisual)
        {
            var consolidacaoCC = audioVisual.GerarConsolidacaoConexa();
            var consolidacaoAA = audioVisual.GerarConsolidacaoAutoral();

            var dadosConsolidacaoAA = InsereDadosConsolidacao(audioVisual);
            var dadosConsolidacaoDtlAA = InsereDadosConsolidacaoDtl(audioVisual, dadosConsolidacaoAA, "AA");

            var dadosConsolidacaoCC = InsereDadosConsolidacao(audioVisual);
            var dadosConsolidacaoDtlCC = InsereDadosConsolidacaoDtl(audioVisual, dadosConsolidacaoCC, "CC");

            return ConsolidacaoDTO.Create(consolidacaoCC, consolidacaoAA, dadosConsolidacaoAA, dadosConsolidacaoDtlAA, dadosConsolidacaoCC, dadosConsolidacaoDtlCC);
        }

