InsereDadosConsolidacaoDtl.raw:
private static DistConsolidacaoDtl InsereDadosConsolidacaoDtl(AudioVisualGloboplay audioVisual, DistConsolidacao consolidacao, string parteRepresentada)
        {
            var valorRendimento = 0M;

            if (parteRepresentada == "AA")
                valorRendimento = (audioVisual.Competencia.VlrVerbaAutoral * audioVisual.VlrFatorDistribuicao) / audioVisual.Competencia.VerbaTotal;
            else
                valorRendimento = (audioVisual.Competencia.VlrVerbaConexa * audioVisual.VlrFatorDistribuicao) / audioVisual.Competencia.VerbaTotal;

            var consolidacaoDtl = new DistConsolidacaoDtl
            {
                ConsolidDistrib = consolidacao,
                NroExecucoes = audioVisual.NroFatorDistribuicao,
                TipParteRepresentada = parteRepresentada,
                NroExecIncorp = 0,
                VlrRendimentoExecucao = valorRendimento,
                NroExecRefdg = (int)audioVisual.SomaFatorDistribuicao,
                NroExecRefdgInc = 0,
                TipExecucao = "S",
                TipSituacaoExecucao = "L"
            };

            return consolidacaoDtl;
        }

