TitularComPendenciaNaRepresentacao.raw:
public bool TitularComPendenciaNaRepresentacao()
        {
            if (Representacao == null)
                return true;

            if (Representacao.CodSociedade == 0 || (Representacao.IndNacionalCol == "N" && Representacao.CodSociedadeRepresenta == null) || Representacao.IndSituacaoSociedade != "A")
                return true;

            if (Representacao.IndNacionalCol == "N")
            {
                if (Representacao.IndSituacaoSociedadeRepresenta != "A")
                    return true;
            }

            return false;
        }

