CalculaFonogramas.raw:
public async Task CalculaFonogramas(AudioVisualGloboplay audioVisual)
        {
            if (audioVisual != null)
            {
                bool deveReterPorSemRegistros =
                    !(audioVisual.CodCapiEpisodio == null && audioVisual.IndSituacaoAudiovisual.Trim() == "P") &&
                    !(audioVisual.ObrasMusicais.Count() < 1 && audioVisual.CodCapiEpisodio != null);

                if (deveReterPorSemRegistros)
                {
                    var consolidacaoFinal = ConsolidacaoUtils.GerarConsolidacao(audioVisual);

                    consolidacaoFinal.DadosConsolidacaoAA.CodConsolidDistrib = audioVisual.CodigoCosolidacaoAA.Value;
                    consolidacaoFinal.DadosConsolidacaoCC.CodConsolidDistrib = audioVisual.CodigoCosolidacaoCC.Value;
                    consolidacaoFinal.DadosConsolidacaoDtlAA.CodConsolidDistribDtl = audioVisual.CodigoCosolidacaoAADtl.Value;
                    consolidacaoFinal.DadosConsolidacaoDtlCC.CodConsolidDistribDtl = audioVisual.CodigoCosolidacaoCCDtl.Value;

                    foreach (var fonograma in audioVisual.Fonogramas)
                    {

                        if (fonograma?.TipSituacaoFono == "L")
                        {
                            if (audioVisual.TitularesFonograma.Where(x => x.CodFonograma == fonograma.Fonograma).Any())
                            {
                                var listaTitularesFonograma = audioVisual.TitularesFonograma.Where(x => x.CodFonograma == fonograma.Fonograma).ToList().DistinctBy(f => f.CodFonogramaTitular);
                                foreach (var titular in listaTitularesFonograma)
                                {
                                    if (titular.CodTitularIdentificado != null)
                                        await _retencaoService.RetencaoPorTitularFonogramaComPendência(audioVisual, titular, fonograma, consolidacaoFinal.ConsolidacaoCC, "TDP", consolidacaoFinal.DadosConsolidacaoDtlCC);
                                    else if (titular.TitularComPendenciaNaRepresentacao())
                                        await _retencaoService.RetencaoPorTitularFonogramaComPendência(audioVisual, titular, fonograma, consolidacaoFinal.ConsolidacaoCC, "TPI", consolidacaoFinal.DadosConsolidacaoDtlCC);
                                    else
                                    {
                                        await _distribuirVerba.DistribuirVerbaFonograma(audioVisual, fonograma, consolidacaoFinal.ConsolidacaoCC, titular, "CC", consolidacaoFinal.DadosConsolidacaoDtlCC);
                                    }
                                }
                            }
                            else
                            {
                                await _retencaoService.RetencaoPorOFonogramaComPendência(audioVisual, fonograma, consolidacaoFinal.ConsolidacaoCC, "FPI", consolidacaoFinal.DadosConsolidacaoDtlCC);
                            }
                        }
                        else
                            await _retencaoService.RetencaoPorOFonogramaComPendência(audioVisual, fonograma, consolidacaoFinal.ConsolidacaoCC, "FPI", consolidacaoFinal.DadosConsolidacaoDtlCC);
                    }
                }
            }
        }

CalculaObrasMusicais.raw:
public async Task CalculaObrasMusicais(AudioVisualGloboplay audioVisual)
        {

            if (audioVisual != null)
            {
                bool deveReterPorSemRegistros =
                   !(audioVisual.CodCapiEpisodio == null && audioVisual.IndSituacaoAudiovisual.Trim() == "P") &&
                   !(audioVisual.ObrasMusicais.Count() < 1 && audioVisual.CodCapiEpisodio != null);

                if (deveReterPorSemRegistros)
                {
                    var consolidacaoFinal = ConsolidacaoUtils.GerarConsolidacao(audioVisual);

                    consolidacaoFinal.DadosConsolidacaoAA.CodConsolidDistrib = audioVisual.CodigoCosolidacaoAA.Value;
                    consolidacaoFinal.DadosConsolidacaoCC.CodConsolidDistrib = audioVisual.CodigoCosolidacaoCC.Value;
                    consolidacaoFinal.DadosConsolidacaoDtlAA.CodConsolidDistribDtl = audioVisual.CodigoCosolidacaoAADtl.Value;
                    consolidacaoFinal.DadosConsolidacaoDtlCC.CodConsolidDistribDtl = audioVisual.CodigoCosolidacaoCCDtl.Value;

                    foreach (var obra in audioVisual.ObrasMusicais)
                    {
                        if (obra?.TipSituacaoCadastral == "ID")
                        {
                            if (audioVisual.TitularesObra.Where(x => x.CodObraMusEcad == obra.CodObraMusEcad).Count() >= 1)
                            {
                                var listaTitularesObra = audioVisual.TitularesObra.Where(x => x.CodObraMusEcad == obra.CodObraMusEcad).ToList().DistinctBy(f => f.CodObraTitular);

                                foreach (var titular in listaTitularesObra)
                                {
                                    if (titular.CodTitularIdentificado != null)
                                        await _retencaoService.RetencaoPorTitularObraComPendência(audioVisual, titular, obra, consolidacaoFinal.ConsolidacaoAA, "TDP", consolidacaoFinal.DadosConsolidacaoDtlAA);
                                    else if (titular.TitularComPendenciaNaRepresentacao())
                                        await _retencaoService.RetencaoPorTitularObraComPendência(audioVisual, titular, obra, consolidacaoFinal.ConsolidacaoAA, "TPI", consolidacaoFinal.DadosConsolidacaoDtlAA);
                                    else
                                        await _distribuirVerba.DistribuirVerbaObra(audioVisual, obra, consolidacaoFinal.ConsolidacaoAA, titular, "AA", consolidacaoFinal.DadosConsolidacaoDtlAA);
                                }
                            }
                            else
                            {
                                await _retencaoService.RetencaoPorObraMusicalComPendência(audioVisual, obra, consolidacaoFinal.ConsolidacaoAA, "OPI", consolidacaoFinal.DadosConsolidacaoDtlAA);
                            }
                        }
                        else
                            await _retencaoService.RetencaoPorObraMusicalComPendência(audioVisual, obra, consolidacaoFinal.ConsolidacaoAA, "OPI", consolidacaoFinal.DadosConsolidacaoDtlAA);
                    }
                }
            }
        }

CalculaEGeraRetencao.raw:
public async Task CalculaEGeraRetencao(AudioVisualGloboplay audioVisual)
        {
            if (audioVisual != null)
            {

                var consolidacaoFinal = ConsolidacaoUtils.GerarConsolidacao(audioVisual);

                if (audioVisual.CodCapiEpisodio == null && audioVisual.IndSituacaoAudiovisual.Trim() == "P")
                    await _retencaoService.RetencaoPorAudiovisualSemRegistrosSemCache(audioVisual, "API", consolidacaoFinal.DadosConsolidacaoDtlAA, consolidacaoFinal.DadosConsolidacaoDtlCC);
                else if (audioVisual.ObrasMusicais.Count() < 1 && audioVisual.CodCapiEpisodio != null)
                    await _retencaoService.RetencaoPorAudiovisualSemRegistrosSemCache(audioVisual, "CPI", consolidacaoFinal.DadosConsolidacaoDtlAA, consolidacaoFinal.DadosConsolidacaoDtlCC);
                else
                {
                    await context.AddAsync(consolidacaoFinal.DadosConsolidacaoAA);
                    await context.AddAsync(consolidacaoFinal.DadosConsolidacaoDtlAA);

                    await context.AddAsync(consolidacaoFinal.DadosConsolidacaoCC);
                    await context.AddAsync(consolidacaoFinal.DadosConsolidacaoDtlCC);
                }

                //TODO: mover isso para buscar os dados uma vez apenas no woeker
                var r = await context.SdAvRolObraFonoAudio.Where(x => x.CodObraFonoAudio == audioVisual.CodObraFonoAudio).ExecuteUpdateAsync(setters => setters
                                                                                                            .SetProperty(r => r.VlrFatorDistribuicao, audioVisual.VlrFatorDistribuicao));
                await context.SaveChangesAsync();

                //_cacheService.Adicionar(consolidacaoFinal.DadosConsolidacaoDtlAA);
                //_cacheService.Adicionar(consolidacaoFinal.DadosConsolidacaoDtlCC);

                audioVisual.CodigoCosolidacaoAA = consolidacaoFinal.DadosConsolidacaoAA.CodConsolidDistrib;
                audioVisual.CodigoCosolidacaoCC = consolidacaoFinal.DadosConsolidacaoCC.CodConsolidDistrib;
                audioVisual.CodigoCosolidacaoCCDtl = consolidacaoFinal.DadosConsolidacaoDtlCC.CodConsolidDistribDtl;
                audioVisual.CodigoCosolidacaoAADtl = consolidacaoFinal.DadosConsolidacaoDtlAA.CodConsolidDistribDtl;

                await context.AddAsync(InsertDistRolOrigemExecucao(audioVisual));

                if (consolidacaoFinal.ConsolidacaoAA != null && consolidacaoFinal.ConsolidacaoAA.Count() > 0)
                {
                    await context.DistPonto.AddRangeAsync(InsertDistPonto(audioVisual, consolidacaoFinal.ConsolidacaoAA, consolidacaoFinal.DadosConsolidacaoDtlAA.CodConsolidDistribDtl));
                    await context.DistExecucaoSumario.AddRangeAsync(InsertDistExecucaoSumario(audioVisual, consolidacaoFinal.ConsolidacaoAA, consolidacaoFinal.DadosConsolidacaoDtlAA.CodConsolidDistribDtl));
                    await context.DistConsolidacaoAud.AddRangeAsync(InsertConsolidacaoAUD(audioVisual, consolidacaoFinal.ConsolidacaoAA, consolidacaoFinal.DadosConsolidacaoDtlAA.CodConsolidDistribDtl));
                }

                if (consolidacaoFinal.ConsolidacaoCC != null && consolidacaoFinal.ConsolidacaoCC.Count() > 0)
                {
                    await context.DistPonto.AddRangeAsync(InsertDistPonto(audioVisual, consolidacaoFinal.ConsolidacaoCC, consolidacaoFinal.DadosConsolidacaoDtlCC.CodConsolidDistribDtl));
                    await context.DistExecucaoSumario.AddRangeAsync(InsertDistExecucaoSumario(audioVisual, consolidacaoFinal.ConsolidacaoCC, consolidacaoFinal.DadosConsolidacaoDtlCC.CodConsolidDistribDtl));
                    await context.DistConsolidacaoAud.AddRangeAsync(InsertConsolidacaoAUD(audioVisual, consolidacaoFinal.ConsolidacaoCC, consolidacaoFinal.DadosConsolidacaoDtlCC.CodConsolidDistribDtl));
                }

                await context.SaveChangesAsync();

                await SendMessageToTopic(audioVisual);
            }
        }

SendMessageToTopic.raw:
private async Task SendMessageToTopic(AudioVisualGloboplay audioVisual)
        {
            var message = System.Text.Json.JsonSerializer.Serialize(audioVisual);

            if (string.IsNullOrEmpty(message))
            {
                _logger.LogError("Message to send to the queue is null or empty.");
                return;
            }

            try
            {
                var busMessage = new ServiceBusMessage(message);

                await sender.SendMessageAsync(busMessage);

                Console.WriteLine($"Message sent: {message}");

                _logger.LogInformation("Message sent to the queue: {message}", message);
            }
            catch
            {
                throw;
            }
            finally
            {
                if (sender != null)
                {
                    await sender.CloseAsync();
                }
            }
        }

TemFonogramasComMaisDe50Titulares.raw:
private bool TemFonogramasComMaisDe50Titulares(AudioVisualGloboplay audioVisual)
        {
            // Verificar se há fonogramas ou titulares nulos
            if (audioVisual.Fonogramas == null || audioVisual.TitularesFonograma == null)
            {
                return false;
            }

            // Criar um HashSet com os códigos dos fonogramas válidos para busca rápida
            var codigosFonogramas = new HashSet<long>(
                audioVisual.Fonogramas.Where(f => f != null).Select(f => f.Fonograma.Value)
            );

            if (codigosFonogramas.Count == 0)
            {
                return false;
            }

            // Agrupar os titulares por CodFonograma e verificar se algum grupo tem mais de 50 titulares
            var fonogramasComMaisDe50Titulares = audioVisual.TitularesFonograma
                .Where(t => codigosFonogramas.Contains(t.CodFonograma))  // Apenas titulares que estão no HashSet de fonogramas
                .GroupBy(t => t.CodFonograma)  // Agrupa por CodFonograma
                .Any(g => g.Count() > 50);     // Verifica se algum grupo tem mais de 50 titulares

            return fonogramasComMaisDe50Titulares;
        }

