public DistConsolidacaoDtl BuscarConsolidacao(long id)
        {
            // Tenta obter os dados do cache
            if (_cache.TryGetValue(CacheKey, out IEnumerable<DistConsolidacaoDtl> consolidacoes))
            {
                return consolidacoes.FirstOrDefault(x => x.CodConsolidDistribDtl == id);
            }

            // Se os dados não estiverem no cache, retorna null ou outra lógica
            return null;
        }

Adicionar.raw:
public void Adicionar(DistConsolidacaoDtl consolidacao)
        {
            if (_cache.TryGetValue(CacheKey, out IList<DistConsolidacaoDtl> consolidacoes))
            {
                // Cria uma nova lista baseada na lista cacheada
                var consolidacoesAtualizadas = new List<DistConsolidacaoDtl>(consolidacoes);

                // Verifica se o item já existe na lista
                if (!consolidacoesAtualizadas.Any(f => f.CodConsolidDistribDtl == consolidacao.CodConsolidDistribDtl))
                {
                    // Adiciona o novo item
                    consolidacoesAtualizadas.Add(consolidacao);
                }

                // Atualiza o cache com a lista modificada
                _cache.Set(CacheKey, consolidacoesAtualizadas, _cacheOptions);
            }
        }

