public async Task StreamDetalhesRelataorioDemonstrativoTitularAsync(HeaderDemonstrativoTitular header, Func<DetalhesDemonstrativoTitular, Task> processItem)
        {
            using (var connection = GetConnection())
            {
                var query = @"
        SELECT 
            convert(varchar, cod_obrafonoreal) + ' - ' + nom_tituloobra as ObraMusical,
            dsc_rubrica as Rubrica,
            dat_periododist as Periodo,
            VLR_RENDOBRA as Rendimento,
            pct_participacaotit as Percentual,
            VLR_TITULAR as Rateio,
            nro_execucoes as Execucoes,
            COD_CATEGORIA as Cat,
            cod_caracteristicadist as Car,
            NOM_TITULOAUDIOORIG as ObraAudiovisual,
            NRO_CAPITULOAUDIO + ' - ' + NOM_CAPITULOAUDIOORIG as Capitulo,
            GRA_ISRC as Isrc,
            sgl_sociedade as SiglaSociedade,
            mes_pagamento as MesAno,
            cod_titularecad as TitularidadeEcad
        FROM REL_DEMONSTRATIVO_TITULAR
        WHERE sgl_sociedade = @SiglaSociedade AND mes_pagamento = @MesAno AND cod_titularecad = @TitularidadeEcad";

                var parameters = new
                {
                    SiglaSociedade = header.Associacao,
                    MesAno = header.MesAno,
                    TitularidadeEcad = header.Codigo
                };

                // Stream results one row at a time
                var reader = await connection.ExecuteReaderAsync(query, parameters);
                while (await reader.ReadAsync())
                {
                    var detalhe = new DetalhesDemonstrativoTitular
                    {
                        ObraMusical = reader["ObraMusical"].ToString(),
                        Rubrica = reader["Rubrica"].ToString(),
                        Periodo = reader["Periodo"].ToString(),
                        Rendimento = reader["Rendimento"].ToString(),
                        Percentual = reader["Percentual"].ToString(),
                        Rateio = reader["Rateio"].ToString(),
                        Execucoes = reader["Execucoes"].ToString(),
                        Cat = reader["Cat"].ToString(),
                        Car = reader["Car"].ToString(),
                        ObraAudiovisual = reader["ObraAudiovisual"].ToString(),
                        Capitulo = reader["Capitulo"].ToString(),
                        Isrc = reader["Isrc"].ToString(),
                        SiglaSociedade = reader["SiglaSociedade"].ToString(),
                        MesAno = reader["MesAno"].ToString(),
                        TitularidadeEcad = reader["TitularidadeEcad"].ToString()
                    };

                    await processItem(detalhe); // Process each row one by one
                }
            }
        }

