Do.raw:
public async Task Do()
    {
        var stopWatch = Stopwatch.StartNew();

        try
        {
            _logger.LogInformation("Início");

            var demonstrativos = await _dapperDAO.GetAllDetalhesRelataorioDemonstrativoTitular();
            var headers = await _dapperDAO.GetHeaderRelataorioDemonstrativoTitular();

            var totalHeaders = headers.Count();

            _logger.LogInformation($"{totalHeaders} headers to process");

            var headersWithDetails = headers.Select(header => System.Text.Json.JsonSerializer.Serialize(new HeaderDemonstrativoTitular
            {
                MesAno = header.MesAno,
                Codigo = header.Codigo,
                Associacao = header.Associacao,
                Total = header.Total,
                Nome = header.Nome,
                Pseudonimo = header.Pseudonimo,
                DetalhesDemonstrativoTitulares = demonstrativos
                    .Where(d => d.MesAno == header.MesAno &&
                                d.TitularidadeEcad == header.Codigo &&
                                d.SiglaSociedade == header.Associacao)
            }));


            List<Task> tasks = new List<Task>();
            var semaphore = new SemaphoreSlim(50);
            var count = 1;

            foreach (var header in headersWithDetails)
            {
                await semaphore.WaitAsync(); // Aguarda a liberação de um slot no semáforo
                var currentCount = count; // Captura o valor atual de 'count' para exibição no log

                tasks.Add(Task.Run(async () =>
                {
                    try
                    {
                        // Salva o JSON em um Blob no Azure Storage
                        var blobUrl = await SaveToBlobAsync(header);

                        _logger.LogInformation($"Sent message with Blob URL for batch {currentCount}.");

                    }
                    finally
                    {
                        semaphore.Release(); // Libera o semáforo após a execução
                    }
                }));

                count++;
            }

            await Task.WhenAll(tasks);

            _logger.LogInformation($"Tempo até agora: {stopWatch.Elapsed.TotalMinutes}");
            _logger.LogInformation($"{stopWatch.Elapsed.TotalMinutes} minutes - Processed all headers.");
        }
        catch (Exception ex)
        {
            _logger.LogError($"{stopWatch.Elapsed.TotalMinutes} minutes - Error: {ex}");
        }
        stopWatch.Stop();
    }

SaveToBlobAsync.raw:
private async Task<string> SaveToBlobAsync(string jsonPayload)
    {
        // Nome do container
        var containerName = "payloads";

        // Nome do arquivo (você pode alterar conforme sua lógica)
        var blobName = $"{Guid.NewGuid()}.json";

        // Acessa o container (cria o container se não existir)
        var containerClient = _blobServiceClient.GetBlobContainerClient(containerName);

        // Acessa o blob (arquivo JSON)
        var blobClient = containerClient.GetBlobClient(blobName);

        // Carrega o payload como stream para o blob
        using var memoryStream = new MemoryStream(System.Text.Encoding.UTF8.GetBytes(jsonPayload));
        await blobClient.UploadAsync(memoryStream, true);

        // Retorna o link para o blob (você pode ajustar o formato do URL se precisar de um SAS Token)
        return blobClient.Uri.ToString();
    }

