CalcularFatorDistribuicao.raw:
public IEnumerable<AudioVisual> CalcularFatorDistribuicao(Competencia? competencia, IEnumerable<AudioVisual> audioVisuais)
        {
            var somaFatorDistribuicao = audioVisuais.Sum(x => x.NroFatorDistribuicao);
            var verbaTotal = competencia.VlrVerbaAutoral + competencia.VlrVerbaConexa;
            var ponto = verbaTotal / somaFatorDistribuicao;

            return audioVisuais.Select(x =>
                new AudioVisual
                {
                    CodCapiEpisodio = x.CodCapiEpisodio,
                    CodObraAudioEcad = x.CodObraAudioEcad,
                    NroFatorDistribuicao = x.NroFatorDistribuicao,
                    VlrFatorDistribuicao = ponto * x.NroFatorDistribuicao,
                    SomaFatorDistribuicao = somaFatorDistribuicao,
                    VlrPonto = ponto,
                    NomTituloOrig = x.NomTituloOrig,
                    NomTituloTrad = x.NomTituloTrad,
                    IndSituacaoAudiovisual = x.IndSituacaoAudiovisual,
                    NroCapEpisodio = x.NroCapEpisodio,
                    NomCapEpisodioOrig = x.NomCapEpisodioOrig,
                    NomCapEpisodioTraduzido = x.NomCapEpisodioTraduzido,
                    CodRolMusical = x.CodRolMusical,
                    DatExibicaoAudio = x.DatExibicaoAudio,
                    CodObraFonoAudio = x.CodObraFonoAudio,
                    DatInicioRol = x.DatInicioRol,
                    DatFimRol = x.DatFimRol,
                    CodFonteRol = x.CodFonteRol,
                    CodTipRol = x.CodTipRol,
                    CodOrigemRol = x.CodOrigemRol,
                    CodUsuario = x.CodUsuario,
                    TipCaracteristicadist = x.TipCaracteristicadist
                }
            ).ToList();
        }

