public async Task SendMessagesAsync(IEnumerable<AudioVisual> messages, Competencia competencia, int codigoDistribuicao, int codDistRol)
        {
            var options = new ParallelOptions()
            {
                MaxDegreeOfParallelism = Environment.ProcessorCount
            };

            await Parallel.ForEachAsync(messages, options, async (message, token) =>
            {
                try
                {
                    message.Competencia = competencia;
                    message.CodDistribuicao = codigoDistribuicao;

                    ServiceBusMessage serviceBusMessage = new ServiceBusMessage(JsonSerializer.Serialize(message));

                    await sender.SendMessageAsync(serviceBusMessage, token);
                    Console.WriteLine($"Message sent: {message}");
                }
                catch (Exception ex)
                {
                    _telemetryClient.TrackTrace(ex.ToString());
                    Console.WriteLine($"Falhou: {ex.Message}");
                    throw;
                }
            });
        }

