ProcessMessagesAsync.raw:
private async Task ProcessMessagesAsync()
        {
            ServiceBusReceiver receiver = _serviceBusClient.CreateReceiver("obrasmusicaisgloboplay");

            try
            {
                while (true)
                {
                    var messages = await receiver.ReceiveMessagesAsync(maxMessages: 100, maxWaitTime: TimeSpan.FromSeconds(1));

                    if (messages.Any())
                    {
                        var processingTasks = new List<Task>();

                        foreach (var message in messages)
                        {
                            processingTasks.Add(ProcessMessageAsync(receiver, message));
                        }

                        await Task.WhenAll(processingTasks);
                    }
                }
            }
            finally
            {
                await receiver.CloseAsync();
            }
        }

ProcessMessageAsync.raw:
private async Task ProcessMessageAsync(ServiceBusReceiver receiver, ServiceBusReceivedMessage message)
        {
            try
            {
                var audioVisual = JsonConvert.DeserializeObject<AudioVisual>(message.Body.ToString());

                // ALTERAR A CONDIÇÃO DO FILTRO ANTES DE RODAR
                audioVisual.ObrasMusicais = fonogramas
                                                .Where(x => x.CodObraAudioEcad == audioVisual.CodObraAudioEcad && x.CodCapEpisodio == audioVisual.CodCapiEpisodio)
                                                .Select(y => new MusicaAudio
                                                {
                                                    CodColetivo = y.CodColetivo,
                                                    TipSituacaoFono = y.TipSituacaoFono,
                                                    CaracteristicaDistribuicao = y.CaracteristicaDistribuicao,
                                                    CaracteristicaRegistro = y.CaracteristicaRegistro,
                                                    CodCapEpisodio = y.CodCapEpisodio,
                                                    CodGra = y.CodGra,
                                                    CodIsrc = y.CodIsrc,
                                                    CodObraAudioEcad = y.CodObraAudioEcad,
                                                    CodObraMusEcad = y.CodObraMusEcad,
                                                    Fonograma = y.Fonograma,
                                                    ParteRepresentada = y.ParteRepresentada,
                                                    QtdDuracaoMusica = y.QtdDuracaoMusica
                                                }).ToList();

                await SendMessageToQueue(JsonConvert.SerializeObject(audioVisual));
                await receiver.CompleteMessageAsync(message);
                Console.WriteLine($"Mensagem processada.");
            }
            catch (Exception ex)
            {
                Console.WriteLine($"Error processing message: {ex.Message}");
            }
        }

SendMessageToQueue.raw:
private async Task SendMessageToQueue(string message)
        {
            if (string.IsNullOrEmpty(message))
            {
                _logger.LogError("Message to send to the queue is null or empty.");
                return;
            }

            ServiceBusSender sender = _serviceBusClient.CreateSender(_outputQueueName);

            try
            {
                ServiceBusMessage busMessage = new ServiceBusMessage(message);
                await sender.SendMessageAsync(busMessage);

                _logger.LogInformation("Message sent to the queue: {message}", message);
            }
            finally
            {
                await sender.DisposeAsync();
            }
        }

