public async Task<bool> senderMessage(dynamic message, string connectionString, string queue)
        {
            ServiceBusClient client;
            ServiceBusSender sender;

            var clientOptions = new ServiceBusClientOptions()
            {
                TransportType = ServiceBusTransportType.AmqpWebSockets
            };
            client = new ServiceBusClient(connectionString, clientOptions);
            sender = client.CreateSender(queue);

            using ServiceBusMessageBatch messageBatch = await sender.CreateMessageBatchAsync();
            messageBatch.TryAddMessage(new ServiceBusMessage(JsonSerializer.Serialize(message)));

            try
            {
                await sender.SendMessagesAsync(messageBatch);
                Console.WriteLine($"messages has been published to the queue.");
            }
            finally
            {
                await sender.DisposeAsync();
                await client.DisposeAsync();
            }

            return true;
        }

