                        var subscription2 = "obrasmusicais";

                        // Verificar a primeira subscription
                        var subscription1Details = await _adminClient.GetSubscriptionRuntimePropertiesAsync(topicName, subscription1);
                        long subscription1MessageCount = subscription1Details.Value.ActiveMessageCount;

                        // Verificar a segunda subscription
