                        {
                            _telemetryClient.TrackEvent("ExecucaoIniciada", new Dictionary<string, string>
                            {
                                { "timestamp", DateTime.UtcNow.ToString("o") }
                            });
                            _logger.LogInformation("ExecucaoIniciada");
                            iniciouExecucao = true;
                        }
                        else
                        {
                            if (iniciouExecucao)
