SendToDeadletter.raw:
private async Task SendToDeadletter(string message, Exception ex)
        {
            var deadLetterErrorDescription = ex.StackTrace;

            if (ex.InnerException != null)
                deadLetterErrorDescription += $" {ex.InnerException.ToString()}";

            deadLetterErrorDescription = deadLetterErrorDescription?.Length > 4000 ?  deadLetterErrorDescription?.Substring(0, 4000) : deadLetterErrorDescription;

            var failedMessage = new ServiceBusMessage(message)
            {
                ApplicationProperties = { { "Exception", ex.Message }, { "StackTrace", deadLetterErrorDescription } }
            };

            // Envie a mensagem para a fila de falhas personalizada
            await _sender.SendMessageAsync(failedMessage);
        }

