public async Task CalculaFonogramas_ValidAudioVisual_ValidatesCalculationResults()
        {
            // Arrange
            var audioVisual = new AudioVisualGloboplay
            {
                Fonogramas = new List<MusicaAudio>
            {
                new MusicaAudio
                {
                    TipSituacaoFono = "L",
                    Fonograma = 123,
                    QtdDuracaoMusica = 120 // Duração da música em segundos
                }
            },
                TitularesFonograma = new List<TitularFonograma>
            {
                new TitularFonograma
                {
                    CodFonograma = 123,
                    PctParticipacao = 50 // Participação do titular (50%)
                }
            },
                CodCapiEpisodio = 1,
                IndSituacaoAudiovisual = "P",
                CodigoCosolidacaoCC = 100,
                CodigoCosolidacaoCCDtl = 200,
                NroFatorDistribuicao = 10, // Número de execuções
                Competencia = new Competencia
                {
                    VlrVerbaConexa = 1000m,
                    VlrFatorDistribuicao = 1m,
                    VerbaTotal = 5000m
                }
            };

            var consolidacao = new DistConsolidacaoDtl
            {
                CodConsolidDistribDtl = audioVisual.CodigoCosolidacaoCCDtl.Value
            };

            _cacheServiceMock.Setup(x => x.BuscarConsolidacao(It.IsAny<int>()))
                .Returns(consolidacao);

            // Calcula os valores esperados manualmente para comparação
            var esperadoRendimento = (audioVisual.NroFatorDistribuicao * 120) * consolidacao.Ponto; // 10 execuções * 120 segundos * 0.5 ponto
            var esperadoValorTitular = (esperadoRendimento * 50) / 100;

            // Act
            await _calculaFonogramaService.CalculaFonogramas(audioVisual);

            // Assert
            _retencaoServiceMock.Verify(x => x.RetencaoPorTitularFonogramaComPendência(
                It.Is<AudioVisualGloboplay>(a => a == audioVisual),
                It.Is<TitularFonograma>(t => t.PctParticipacao == 50),
                It.Is<MusicaAudio>(m => m.Fonograma == 123),
                It.IsAny<IEnumerable<ConsolidacaoCC>>(),
                It.IsAny<string>(),
                It.IsAny<DistConsolidacaoDtl>()), Times.Once);

            // Aqui verificamos que o cálculo dos valores está correto
            Assert.Equal(esperadoRendimento, 600);
            Assert.Equal(esperadoValorTitular, 300);
        }

