ProcessMessagesAsync.raw:
private async Task ProcessMessagesAsync(string queueName, Func<ServiceBusReceiver, ServiceBusReceivedMessage, Task> processMessage, CancellationToken stoppingToken)
        {
            var receiver = _serviceBusClient.CreateReceiver(queueName);

            try
            {
                while (!stoppingToken.IsCancellationRequested)
                {
                    var messages = await receiver.ReceiveMessagesAsync(maxMessages: 100, maxWaitTime: TimeSpan.FromSeconds(1), cancellationToken: stoppingToken);

                    if (messages.Any())
                    {
                        Console.WriteLine("Tem mensagem");
                        var parallelOptions = new ParallelOptions
                        {
                            MaxDegreeOfParallelism = Environment.ProcessorCount
                        };

                        await Parallel.ForEachAsync(messages, parallelOptions, async (msg, ct) =>
                        {
                            await processMessage(receiver, msg);
                        });
                    }
                    else
                    {
                        Console.WriteLine("Não Tem mensagem");
                    }
                }
            }
            finally
            {
                await receiver.CloseAsync();
            }
        }

